package learning;

public class LearnFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 50; i++) {
			if (i % 2 == 0) {
				System.out.println(i);
			}
		}
		System.out.println("****************");
		for (int j = 0; j < 10; j++) {
			System.out.println(j);
			++j;

		}

	}
}
